import React from "react"
// import react hooks (useEffect, useState) -> needed for data and auto-refreshing
import { useEffect, useState } from "react"
// axios -> used to make HTTP requests to our backend
import axios from "axios"
// for data visualization
import { Line } from "react-chartjs-2"
import "chart.js/auto"

// stores the API endpoint so we can fetch sensor data
const API_URL = "http://localhost:5001/api/readings" // BACKEND API URL

function App() {
  // creates energyData state -> holds sensor data, initially it's an empty array ([])
  const [energyData, setEnergyData] = useState([])

  // makes the GET request to API Route in our backend & updates the energy state with the response
  const fetchData = async () => {
    try {
      const response = await axios.get(`${API_URL}?t=${new Date().getTime()}`, {
        headers: { "Cache-Control": "no-cache" },
      })
      console.log("Fetched Data:", response.data) // Debug log

      if (Array.isArray(response.data)) {
        setEnergyData(response.data)
      } else {
        console.error("Invalid API response format:", response.data)
      }
    } catch (error) {
      console.error("Error fetching the response:", error)
    }
  }
  // calls fetch data once when the component loads & sets a data refresh interval of every 5 seconds
  useEffect(() => {
    fetchData()
    const interval = setInterval(fetchData, 5000) // refresh every 5s
    return () => clearInterval(interval)
  })

  // prepare data for visualization, sets timetstamp on x-axis and energy values on y-axis and defines line chart style
  const sortedData = [...energyData].sort(
    (a, b) => new Date(a.timestamp) - new Date(b.timestamp)
  )

  const chartData =
    energyData.length > 0
      ? {
          labels: sortedData.map((entry) =>
            new Date(entry.timestamp).toLocaleTimeString()
          ),
          datasets: [
            {
              label: "HVAC Floor 1",
              data: sortedData
                .filter((entry) => entry.sensor === "hvac/floor1")
                .map((entry) => entry.value),
              borderColor: "rgb(255, 99, 132)",
              backgroundColor: "rgba(255, 99, 132, 0.2)",
              borderWidth: 3, // Ensure thick lines
              pointRadius: 5, // Ensure points are visible
            },
            {
              label: "HVAC Floor 2",
              data: sortedData
                .filter((entry) => entry.sensor === "hvac/floor2")
                .map((entry) => entry.value),
              borderColor: "rgb(54, 162, 235)",
              backgroundColor: "rgba(54, 162, 235, 0.2)",
              borderWidth: 3,
              pointRadius: 5,
            },
            {
              label: "Lighting Floor 1",
              data: sortedData
                .filter((entry) => entry.sensor === "lighting/floor1")
                .map((entry) => entry.value),
              borderColor: "rgb(255, 206, 86)",
              backgroundColor: "rgba(255, 206, 86, 0.2)",
              borderWidth: 3,
              pointRadius: 5,
            },
            {
              label: "Lighting Floor 2",
              data: sortedData
                .filter((entry) => entry.sensor === "lighting/floor2")
                .map((entry) => entry.value),
              borderColor: "rgb(75, 192, 192)",
              backgroundColor: "rgba(75, 192, 192, 0.2)",
              borderWidth: 3,
              pointRadius: 5,
            },
          ],
        }
      : { labels: [], datasets: [] } // Fix: Prevent empty dataset errors

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // Allows chart to scale properly
    elements: {
      line: {
        tension: 0.3, // Slight smoothing for better visuals
        borderWidth: 2,
      },
      point: {
        radius: 4, // Ensures visibility of points
      },
    },
    scales: {
      x: {
        beginAtZero: false,
        type: "time", // âœ… Ensures timestamps are evenly spread
        time: {
          unit: "second", // âœ… Distributes points based on time
          tooltipFormat: "HH:mm:ss", // âœ… Tooltip shows full timestamp
          displayFormats: {
            second: "HH:mm:ss",
          },
        },
        ticks: {
          autoSkip: true, // âœ… Forces all timestamps to be displayed
          maxTicksLimit: 8, // âœ… Controls number of labels shown
          align: "center", // âœ… Aligns labels correctly
          maxRotation: 0, // âœ… Keeps text horizontal
          minRotation: 0,
        },
      },
      y: {
        beginAtZero: false, // âœ… Ensures meaningful scaling
        title: {
          display: true,
          text: "Energy Consumption (W)",
        },
      },
    },
  }

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>
        <span img="ðŸ“Š" /> Smart Energy Dashboard
      </h1>
      <div style={{ width: "100%", height: "500px" }}>
        <Line
          data={chartData}
          options={{
            chartOptions,
          }}
        />
      </div>
    </div>
  )
}

export default App
